import{_ as e,c as s,o as r,a as t}from"./app.52ca0d47.js";const c=JSON.parse('{"title":"VitePress\u7684\u521D\u6B21\u5C1D\u8BD5","description":"","frontmatter":{},"headers":[{"level":2,"title":"VitePress\u5438\u5F15\u6211\u7684\u51E0\u4E2A\u5730\u65B9","slug":"vitepress\u5438\u5F15\u6211\u7684\u51E0\u4E2A\u5730\u65B9"},{"level":3,"title":"Vue\u751F\u6001","slug":"vue\u751F\u6001"},{"level":3,"title":"\u7B26\u5408\u4E60\u60EF","slug":"\u7B26\u5408\u4E60\u60EF"},{"level":3,"title":"\u7F16\u8BD1\u5FEB\u901F","slug":"\u7F16\u8BD1\u5FEB\u901F"},{"level":2,"title":"\u4E0EVuePress\u7684\u6BD4\u8F83","slug":"\u4E0Evuepress\u7684\u6BD4\u8F83"},{"level":2,"title":"\u6700\u540E","slug":"\u6700\u540E"}],"relativePath":"explore/a-first-try-to-vitepress.md"}'),i={name:"explore/a-first-try-to-vitepress.md"},a=t('<h1 id="vitepress\u7684\u521D\u6B21\u5C1D\u8BD5" tabindex="-1">VitePress\u7684\u521D\u6B21\u5C1D\u8BD5 <a class="header-anchor" href="#vitepress\u7684\u521D\u6B21\u5C1D\u8BD5" aria-hidden="true">#</a></h1><p>\u4ECA\u5929\u770B\u5230Vite\u53D1\u5E03\u4E863.0\u7248\u672C\u3002\u800C\u5B83\u7684\u7684Realse notes\u91CC\u9762\u63D0\u5230\uFF0C \u73B0\u5728\u5B83\u7684\u6587\u6863\u5168\u90E8\u7531VitePress\u6784\u5EFA\u3002</p><p>\u6211\u4E4B\u524D\u4E00\u76F4\u6CA1\u6709\u5199\u8FC7\u535A\u5BA2\uFF0C\u4F46\u662F\u6211\u4E5F\u786E\u5B9E\u5E38\u5E38\u6709\u5199\u4E00\u70B9\u4EC0\u4E48\u4E1C\u897F\u8BB0\u5F55\u81EA\u5DF1\u60F3\u6CD5\u7684\u65F6\u5019\uFF0C \u5C31\u7B97\u6CA1\u4EC0\u4E48\u4EBA\u770B\u3002\u6B64\u524D\u6211\u6709\u7B80\u5355\u5730\u5C1D\u8BD5\u8FC7Hexo\uFF0CHeroku\u8FD9\u4E9B\u4E1C\u897F\uFF0C \u4F46\u662F\u4E00\u6765\u5BF9\u4ED6\u4EEC\u7684\u6280\u672F\u6808\u6CA1\u4EC0\u4E48\u5174\u8DA3\uFF0C\u4E8C\u6765\u770B\u4E86\u522B\u4EBA\u7528\u5B83\u4EEC\u505A\u51FA\u7684\u7F51\u7AD9\uFF0C \u90FD\u6CA1\u80FD\u8FBE\u5230\u6211\u60F3\u8981\u7684\u6548\u679C\uFF0C\u6240\u4EE5\u5C31\u6CA1\u6DF1\u5165\u4E86\u89E3\u3002</p><p>\u4E0D\u8FC7VitePress\u9A6C\u4E0A\u5438\u5F15\u4E86\u6211\u7684\u6CE8\u610F\u529B\u3002\u7B80\u5355\u5C1D\u8BD5\u4E86\u4E00\u4E0B\uFF0C\u6211\u53D1\u73B0\u5B83\u786E\u5B9E\u633A\u9002\u5408\u6211\u3002 \u6211\u9A6C\u4E0A\u5C31\u7528\u5B83\u642D\u597D\u4E86\u535A\u5BA2\uFF0C\u4E8E\u662F\u8FD9\u7BC7\u6587\u7AE0\u4E5F\u653E\u5728\u5B83\u642D\u5EFA\u7684\u8FD9\u4E2A\u535A\u5BA2\u4E4B\u4E0A\u3002</p><h2 id="vitepress\u5438\u5F15\u6211\u7684\u51E0\u4E2A\u5730\u65B9" tabindex="-1">VitePress\u5438\u5F15\u6211\u7684\u51E0\u4E2A\u5730\u65B9 <a class="header-anchor" href="#vitepress\u5438\u5F15\u6211\u7684\u51E0\u4E2A\u5730\u65B9" aria-hidden="true">#</a></h2><h3 id="vue\u751F\u6001" tabindex="-1">Vue\u751F\u6001 <a class="header-anchor" href="#vue\u751F\u6001" aria-hidden="true">#</a></h3><p>\u5B83\u5C5E\u4E8EVue\u7684\u751F\u6001\u81EA\u7136\u662F\u6700\u521D\u5438\u5F15\u4F4F\u6211\u773C\u7403\u7684\u4E00\u70B9\u3002 \u9996\u5148\u867D\u7136\u6211\u4E5F\u5E76\u4E0D\u5BF9Vue\u6709\u591A\u719F\u6089\uFF0C\u4F46\u76F8\u5BF9\u800C\u8A00\u5B83\u8FD8\u662F\u6211\u6BD4\u8F83\u64C5\u957F\u7684\u6280\u672F\uFF0C \u4E5F\u662F\u6211\u6BD4\u8F83\u611F\u5174\u8DA3\u7684\u524D\u7AEF\u6846\u67B6\u3002\u524D\u9762\u6709\u63D0\u5230\u6211\u4E5F\u5C1D\u8BD5\u8FC7\u7528Hexo\uFF0CHeroku\u505A\u535A\u5BA2\uFF0C \u5176\u5B9E\u653E\u5F03\u5B83\u4EEC\u540E\u6211\u4E5F\u66FE\u60F3\u76F4\u63A5\u7528Vue\u76F4\u63A5\u642D\u5EFA\u535A\u5BA2\uFF0C\u4F46\u662F\u5B9E\u8DF5\u8D77\u6765\u4E0D\u592A\u73B0\u5B9E\uFF0C \u5982\u679C\u5F53\u771F\u4E0D\u4F9D\u9760\u5728\u522B\u4EBA\u7684\u6846\u67B6\u4E4B\u4E0A\uFF0C\u6211\u5C31\u5F97\u81EA\u5DF1\u5B9E\u73B0\u4E00\u4E2A\u4E2A\u529F\u80FD\uFF0C \u4E0D\u7136\u5C31\u662F\u6BCF\u6B21\u90FD\u50CF\u524D\u7AEF\u5F00\u53D1\u90A3\u6837\u6BCF\u6B21\u5199\u4E00\u4E2A\u65B0\u6587\u7AE0\u5C31\u662F\u4E00\u4E2A\u65B0\u9875\u9762\u3002 VitePress\u7ED9\u4E86\u6211\u7B2C\u4E09\u4E2A\u9009\u62E9\uFF0C\u5728Vue\u4E4B\u4E0A\u7ED9\u4E86\u6211\u642D\u5EFA\u9759\u6001\u7AD9\u70B9\u7684\u6846\u67B6\u3002</p><p>VitePress\u4F7F\u7528Vite\uFF0C\u4E5F\u4F7F\u7528Vue3\u642D\u5EFA\uFF0C\u9ED8\u8BA4\u4E3B\u9898\u5F00\u7BB1\u5373\u7528\u3002 \u5B83\u63D0\u4F9B\u4E86\u5728\u5B83\u91CC\u9762\u4F7F\u7528Vue\u7684\u65B9\u6CD5\uFF0C\u8FD9\u5BF9\u4E8E\u719F\u6089Vue\u7684\u4EBA\u6765\u8BF4\u80AF\u5B9A\u662F\u6781\u5927\u4F18\u52BF\u3002</p><h3 id="\u7B26\u5408\u4E60\u60EF" tabindex="-1">\u7B26\u5408\u4E60\u60EF <a class="header-anchor" href="#\u7B26\u5408\u4E60\u60EF" aria-hidden="true">#</a></h3><p>\u867D\u7136\u5728Typora\u8FD9\u4E2A\u77E5\u540D\u7684\u7F16\u8F91\u5668\u79BB\u5F00Beta\u9636\u6BB5\u6B63\u5F0F\u6536\u8D39\u540E\uFF0C \u6211\u662F\u7B2C\u4E00\u65F6\u95F4\u5C31\u4E70\u4E86\u5B83\u7684\u6B63\u7248\u7684\u3002 \u4F46\u662F\u5BF9\u4E8E\u6211\u8FD9\u6837\u4E00\u4E2A\u771F\u6B63\u7684\u91CD\u5EA6Vim\u7528\u6237\u6765\u8BF4\uFF0C \u6700\u504F\u597D\u7684\u6C38\u8FDC\u662F\u7528Vim\u6765\u5199MarkDown\uFF0C\u5C24\u5176\u662F\u82F1\u6587\u6587\u6863\u3002 \u76EE\u524DTypora\u57FA\u672C\u4E0A\u53EA\u88AB\u6211\u7528\u6765\u9884\u89C8\u6211\u672C\u5730\u673A\u5668\u4E0A\u7684.md\u6587\u6863\uFF0C \u5F88\u5C11\u76F4\u63A5\u7528\u5B83\u6765\u5199\u3002</p><p>\u7531\u4E8EVim\u662F\u8DD1\u5728TUI\u4E0B\u9762\u7684\uFF0C \u6240\u4EE5\u4E00\u822C\u800C\u8A00Vim\u7684MarkDown\u9884\u89C8\u90FD\u662F\u63D2\u4EF6\u901A\u8FC7\u6253\u5F00\u4E00\u4E2A\u672C\u5730\u7684\u670D\u52A1\u5668\uFF0C \u5728\u6D4F\u89C8\u5668\u91CC\u9762\u8FDB\u884C\u7684\u3002\u6211\u4F7F\u7528\u7684\u65F6\u5019\u4E00\u822C\u4E5F\u662F\u8FD9\u6837\u4E00\u4E2AVim\u7684\u7A97\u53E3\u5199\uFF0C \u4E00\u4E2A\u6D4F\u89C8\u5668\u7684\u9884\u89C8\u6548\u679C\u7684\u4F7F\u7528\u573A\u666F\u3002</p><p>VitePress\u7ED3\u5408\u4E86MarkDown\uFF0C\u4E5F\u7ED3\u5408\u4E86Vite\u3002\u5C31\u50CF\u6211\u5199\u524D\u7AEF\u9879\u76EE\u65F6\u80FD\u5B9E\u65F6\u9884\u89C8\u9875\u9762\u4E00\u6837\uFF0C VitePress\u53EF\u4EE5\u8BA9\u6211\u5B9E\u65F6\u9884\u89C8MarkDown\u3002 \u6240\u4EE5\u8FD9\u6837\u4E00\u8FB9\u7528Vim\u5199\u535A\u5BA2\u5185\u5BB9\uFF0C\u4E00\u8FB9\u7528\u6D4F\u89C8\u5668\u770B\u6548\u679C\u7684\u4F7F\u7528\u65B9\u5F0F\uFF0C \u4E0D\u4EC5\u7B26\u5408\u591A\u6570\u4EBA\u5199\u4EE3\u7801\u7684\u4E60\u60EF\uFF0C\u4E5F\u7B26\u5408\u6211\u5199MarkDown\u6587\u6863\u7684\u4E60\u60EF\u3002\u975E\u5E38\u9002\u5408\u6211\u3002</p><h3 id="\u7F16\u8BD1\u5FEB\u901F" tabindex="-1">\u7F16\u8BD1\u5FEB\u901F <a class="header-anchor" href="#\u7F16\u8BD1\u5FEB\u901F" aria-hidden="true">#</a></h3><p>Vite\u521A\u63A8\u51FA\u65F6\u4E3B\u6253\u7684\u5C31\u662F\u5FEB\uFF0C\u5C31\u662F\u5F00\u53D1\u65F6\u5019\u7684\u4F53\u9A8C\u3002 \u4E4B\u524D\u6709\u770B\u89C1\u6709\u5F88\u591A\u4EBA\u8BC4\u8BBA\uFF0CVite\u4E0D\u8FC7\u662F\u89E3\u51B3\u4E86\u4E00\u4E2A\u4E0D\u75DB\u4E0D\u75D2\u7684\u95EE\u9898\u3002 \u4E4D\u4E00\u770B\u5F88\u6709\u9053\u7406\uFF0C\u6BD5\u7ADF\u786E\u5B9E\u4E0D\u8FC7\u662F\u7F16\u8BD1\u7684\u901F\u5EA6\u800C\u5DF2\u3002 \u5927\u578B\u7684\u9879\u76EE\u4F8B\u5982Chromium\u52A8\u8F84\u7F16\u8BD1\u4E24\u4E09\u5C0F\u65F6\uFF0C\u4E5F\u6CA1\u6709\u5F71\u54CDChromium\u7684\u6D41\u884C\u3002 \u4F46\u662F\u5B9E\u9645\u4E0A\u5BF9\u4E8E\u524D\u7AEF\u5F00\u53D1\u6765\u8BF4\u7F16\u8BD1\u901F\u5EA6\u7684\u63D0\u5347\u5BF9\u4E8E\u5F00\u53D1\u7684\u4F53\u9A8C\u975E\u5E38\u5927\u3002</p><p>\u76F8\u6BD4\u4E0E\u540E\u7AEF\u5F00\u53D1\u6765\u8BF4\uFF0C\u524D\u7AEF\u5F00\u53D1\u56E0\u4E3A\u4FA7\u91CD\u4E8E\u5B9E\u9645\u7684\u663E\u793A\u6548\u679C\uFF0C \u6240\u4EE5\u9700\u8981\u66F4\u9891\u7E41\u7684\u7F16\u8BD1\uFF0C\u6BCF\u6B21\u66F4\u6539\u4EE3\u7801\u4EE5\u540E\u90FD\u91CD\u65B0\u7F16\u8BD1\u5E76\u66F4\u65B0\uFF08\u4E5F\u5C31\u662F\u5B9E\u65F6\u9884\u89C8\uFF09\u662F\u57FA\u672C\u64CD\u4F5C\uFF0C \u4E0D\u50CF\u540E\u7AEF\u53EA\u9700\u8981\u5728\u6D4B\u8BD5\u548C\u8C03\u8BD5\u7684\u65F6\u5019\u8FDB\u884C\u7F16\u8BD1\u3002 \u6240\u4EE5\u7F16\u8BD1\u901F\u5EA6\u7684\u589E\u52A0\u5BF9\u4E8E\u540E\u7AEF\u5F00\u53D1\u6765\u8BF4\u53EF\u80FD\u662F\u559C\u95FB\u4E50\u89C1\u7684\u559D\u8336\u65F6\u95F4\uFF0C \u4F46\u5BF9\u4E8E\u524D\u7AEF\u6765\u8BF4\u786E\u5B9E\u75DB\u82E6\u7684\u7B49\u5F85\u8FC7\u7A0B\uFF0C\u5C3D\u7BA1\u5F88\u77ED\u6682\uFF0C\u5374\u8DB3\u4EE5\u6253\u7834\u5F00\u53D1\u7684\u8FDE\u8D2F\u6027\u3002</p><p>\u6211\u53C2\u4E0E\u7684\u5B66\u6821\u7684\u4E00\u4E2AWebpack 3\u7684\u9879\u76EE\uFF0C\u662F\u4E00\u4E2A\u4E0A\u4E07\u884C\u4EE3\u7801\u7684Vue 2.0\u5C4E\u5C71\uFF0C \u8FD9\u4E2A\u9879\u76EE\u7684\u4F9D\u8D56\u53EF\u80FD\u4ECE\u9879\u76EE\u521B\u5EFA\u4EE5\u540E\u5C31\u518D\u4E5F\u6CA1\u6709\u5347\u7EA7\u8FC7\uFF0C\u4F7F\u7528\u7684Nuxt\u7684\u7248\u672C\u5C45\u7136\u505C\u7559\u57281.0.0\uFF0C \u800C\u6211\u5199\u4E0B\u8FD9\u7BC7\u6587\u7AE0\u7684\u65F6\u5019\uFF0CWebpack\u7248\u672C\u53F7\u5DF2\u7ECF\u8FED\u4EE3\u52305\uFF0C\u800CNuxt 3.0\u7684RC\u5DF2\u7ECF\u8FED\u4EE3\u56DB\u4E2A\u7248\u672C\u4E86\u3002 \u8FD9\u4E2A\u6709\u5927\u91CF\u5386\u53F2\u5305\u88B1\u7684\u9879\u76EE\u5B9E\u65F6\u9884\u89C8\u7684\u7F16\u8BD1\u901F\u5EA6\u53EA\u80FD\u8BF4\u662F\u4E00\u8A00\u96BE\u5C3D\uFF0C \u6BCF\u6B21\u6539\u597D\u4EE3\u7801\u7B49\u9884\u89C8\u6548\u679C\u51FA\u6765\u7684\u95F4\u9699\u80FD\u591F\u6211\u5FF5\u4E24\u53E5\u8BD7\u4E86\u3002 \u6BCF\u6B21\u5199\u4E86\u4EE3\u7801\u7B49\u51E0\u79D2\u949F\u5EF6\u8FDF\u624D\u80FD\u770B\u6548\u679C\uFF0C\u5F00\u53D1\u4F53\u9A8C\u975E\u5E38\u7CDF\u7CD5\u3002</p><p>\u7684\u786E\uFF0C\u6211\u60F3\u9879\u76EE\u7ECF\u7406\u4E0D\u4F1A\u592A\u5173\u5FC3Vite\u5B83\u7A76\u7ADF\u7F16\u8BD1\u5F97\u5FEB\u4E0D\u5FEB\uFF0C \u4E3B\u8981\u5173\u5FC3\u5B83\u80FD\u4E0D\u80FD\u5B8C\u6210\u4EFB\u52A1\uFF0C\u7A33\u4E0D\u7A33\u5B9A\uFF0C\u7F16\u8BD1\u4EA7\u7269\u7684\u5927\u5C0F\u8FD9\u4E9B\u3002 \u4F46\u662F\u6BCF\u4E2A\u5F00\u53D1\u8005\u5BF9\u4E8E\u8FD9\u4E2A\u7F16\u8BD1\u901F\u5EA6\u7684\u5DEE\u8DDD\uFF0C\u8FD9\u70B9\u5F00\u53D1\u4F53\u9A8C\u7684\u63D0\u5347\uFF0C\u5E94\u8BE5\u90FD\u662F\u6709\u76F4\u63A5\u611F\u53D7\u7684\u3002 \u81F3\u5C11\uFF0C\u6211\u89C9\u5F97\u57FA\u672C\u4E0A\u662F\u7531\u5F00\u53D1\u8005\u7EC4\u6210\u7684\u7684\u5F00\u6E90\u793E\u533A\u5E94\u8BE5\u4F1A\u6BD4\u8F83\u63A8\u5D07Vite\u3002</p><p>\u5C24\u96E8\u6EAA\u53BB\u5E74\u5728\u77E5\u4E4E\u4E0A\u9762\u9884\u8A00\uFF0CVite\u53EF\u4EE5\u5403\u4E0B\u4E00\u5757\u7A33\u5B9A\u7684\u4EFD\u989D\u3002\u8FD9\u4E2A\u9884\u8A00\u786E\u5B9E\u6210\u4E3A\u4E86\u4E8B\u5B9E\u3002 \u9664\u4E86\u524D\u4E24\u5468\u5728\u77E5\u4E4E\u770B\u89C1\u4ED6\u63D0\u5230\u7684\uFF0C\u5DF2\u7ECF\u5C06\u5B98\u65B9\u6784\u5EFA\u5DE5\u5177\u6362\u4E3A\u4E86Vite\u7684Svelte\u4EE5\u5916\uFF0C TypeORM\u4E5F\u9ED8\u8BA4\u4F7F\u7528Vite\u4E86\u3002\u6211\u6700\u8FD1\u5C31\u662F\u7528TypeORM\uFF0C \u6628\u5929\u6211\u5728\u5B83\u7684cli\u65B0\u5EFA\u7684\u9879\u76EE\u7684package.json\u91CC\u9762\u770B\u5230\u4E86Vite\u3002 \u8FD9\u8FD8\u662F\u4EE4\u6211\u633A\u60CA\u8BB6\u7684\u3002</p><h2 id="\u4E0Evuepress\u7684\u6BD4\u8F83" tabindex="-1">\u4E0EVuePress\u7684\u6BD4\u8F83 <a class="header-anchor" href="#\u4E0Evuepress\u7684\u6BD4\u8F83" aria-hidden="true">#</a></h2><p>\u5176\u5B83\u4E0D\u8C08\uFF0C\u5C31VuePress\u7528\u7684\u662FVue 2\u548CWebpack\u800C\u8A00\uFF0C \u6CE8\u5B9A\u4E86\u6211\u8FD9\u6837\u504F\u597D\u65B0\u4E1C\u897F\u7684\u4EBA\u662F\u4E0D\u4F1A\u9009\u62E9\u5B83\u7684\u3002\u51B5\u4E14VuePress\u76EE\u524D\u5DF2\u7ECF\u5904\u4E8E\u7EF4\u62A4\u72B6\u6001\u4E86\u3002</p><p>\u4E0D\u8FC7\uFF0CVuePress\u4E00\u6837\u8FD8\u6709\u4E00\u4E2A\u5347\u7EA7\u7248\uFF0CVuePress-next\u3002 \u6211\u4E00\u5F00\u59CB\u7684\u65F6\u5019\u5C31\u56F0\u60D1\u65E2\u7136VuePress\u5904\u4E8E\u7EF4\u62A4\u72B6\u6001\u4E86\uFF0C \u4E3A\u4EC0\u4E48VitePress\u8FD9\u6837\u4E00\u4E2A\u5728GitHub\u4E0A\u9762\u5C5E\u4E8EVuejs\u800C\u4E0D\u662FVitejs\u7EC4\u7EC7\u7684\u9879\u76EE\uFF0C \u4E3A\u4EC0\u4E48\u4E0D\u6CBF\u7528VuePress\u7684\u540D\u5B57\u4F5C\u4E3A\u4E00\u4E2A\u5168\u65B0\u7684\u91CD\u6784\u7248\u672C\u5462\uFF0C\u539F\u6765VuePress\u81EA\u5DF1\u4E5F\u6709\u5347\u7EA7\u3002</p><p>\u5C24\u96E8\u6EAA\u5728Github\u4E0A\u8BF4\u5B83\u7ED9VitePress\u4E00\u4E2A\u4E0D\u540C\u540D\u5B57\u7684\u76EE\u7684\u662F\u5C1D\u8BD5\u4E00\u4E2A\u5B8C\u5168\u4E0D\u540C\u7684\u7ED3\u6784\uFF0C \u800C\u4E0D\u5FC5\u8003\u8651\u5411\u540E\u517C\u5BB9\u3002\u6839\u636EVitePress\u7684\u63CF\u8FF0\uFF0C VitePress\u4F1A\u201Cmore opinionated and less configurable\u201D\uFF0C \u4E5F\u5C31\u662F\u7075\u6D3B\u5EA6\u4E0D\u5982VuePress\uFF0C\u800C\u4E14\u867D\u7136\u652F\u6301\u5207\u6362\u4E3B\u9898\uFF0C\u672A\u6765\u5374\u672A\u5FC5\u652F\u6301\u63D2\u4EF6\u3002</p><p>\u53E6\u5916\uFF0C\u5C3D\u7BA1\u6709VuePress-next\uFF0C \u4F46VuePress\u7684README\u4E2D\u8BF4\u660E\u9879\u76EE\u5904\u4E8E\u7EF4\u62A4\u72B6\u6001\u7684\u5730\u65B9\u5374\u5E76\u6CA1\u6709\u63D0\u53CA\u8FD9\u4E0B\u4E00\u4E2A\u5927\u7248\u672C\uFF0C \u8FD8\u5EFA\u8BAE\u4F7F\u7528VitePress\u800C\u6CA1\u6709\u63A8\u8350\u4F7F\u7528VuePress-next\u6765\u66FF\u4EE3\u3002 \u53E6\u5916\u5B83\u7684Contributors\u91CC\u9762\u6CA1\u6709\u5C24\u96E8\u6EAA\uFF0C \u6240\u4EE5\u6211\u5BF9VuePress-next\u7684\u53D7\u652F\u6301\u7A0B\u5EA6\u6301\u4E00\u5B9A\u7684\u6000\u7591\u6001\u5EA6\u3002 \u5E76\u4E14\u5C24\u96E8\u6EAA\u8BF4\u672A\u6765\u4ED6\u53EF\u80FD\u8BA9\u7A33\u5B9A\u4EE5\u540E\u7684VitePress\u6210\u4E3AVuePress 3\uFF0C \u57FA\u672C\u4E0A\u7B97\u662F\u7ED9VuePress\u76D6\u4E0A\u4E86\u68FA\u6750\u677F\u3002</p><p>\u8003\u8651\u5230\u6211\u4EE5\u4E0D\u9700\u8981\u5BF9VuePress\u7684\u5411\u540E\u517C\u5BB9\uFF0C\u4E14\u76EE\u524DVitePress\u7684\u6548\u679C\u8DB3\u591F\u597D\uFF0C \u770B\u4E0D\u51FA\u8981\u591A\u4E48\u9AD8\u5EA6\u7684\u5B9A\u5236\uFF0C\u6240\u4EE5\u6211\u9009\u62E9\u4E86VitePress\u3002</p><h2 id="\u6700\u540E" tabindex="-1">\u6700\u540E <a class="header-anchor" href="#\u6700\u540E" aria-hidden="true">#</a></h2><p>\u9009\u7528\u793E\u533A\u9A71\u52A8\u5F00\u6E90\u9879\u76EE\u5F80\u5F80\u6709\u5BF9\u5B83\u4F1A\u4E0D\u4F1A\u6301\u7EED\u66F4\u65B0\u6709\u66F4\u591A\u7684\u62C5\u5FE7\uFF0C \u6BD5\u7ADF\u8C01\u4E5F\u4E0D\u60F3\u8F7B\u6613\u66F4\u6362\u5E95\u5C42\u6846\u67B6\u3002 \u5F53\u524D\uFF0CVitePress\u7684\u6700\u65B0\u7248\u672C\u662F1.0.0-alpha.4\uFF0C\u53EF\u89C1\u8FD8\u51FA\u4E8E\u975E\u5E38\u65E9\u671F\u7684\u9636\u6BB5\u3002 \u4F46\u662F\u6211\u60F3\uFF0C\u65E2\u7136Vite\u7684\u6587\u6863\u5DF2\u7ECF\u66FF\u6362\u6210\u4E86VitePress\u6784\u5EFA\uFF0C \u65E2\u7136VitePress\u5DF2\u7ECF\u5728GitHub\u4E0A\u9762\u6536\u83B7\u4E865k stars\uFF0C\u62FF\u5B83\u7684\u751F\u547D\u5468\u671F\u5E94\u8BE5\u8FD8\u4F1A\u6BD4\u8F83\u957F\uFF0C \u957F\u65F6\u95F4\u5185\u5E94\u8BE5\u4E5F\u8FD8\u5904\u4E8E\u6210\u957F\u671F\u3002\u53E6\u5916\u5B9E\u9645\u4E0A\u6211\u4E5F\u504F\u597D\u66F4\u65B0\u7684\u4E1C\u897F\uFF0C\u54EA\u6015\u5B83\u4E0D\u7A33\u5B9A\uFF0C \u6240\u4EE5\u6211\u6700\u7EC8\u9009\u62E9\u4E86VitePress\u3002</p><p>\u521A\u521A\u628A\u535A\u5BA2\u7684\u4EE3\u7801\u548C\u73AF\u5883\u975E\u5E38\u57FA\u7840\u7684\u90E8\u5206\u5F04\u597D\u4E86\uFF0C\u4E00\u65F6\u4E4B\u95F4\u6709\u5F88\u591A\u611F\u60F3\uFF0C \u8D81\u7740\u7075\u611F\u4E0A\u6765\u4E86\uFF0C\u6587\u6863\u6CA1\u8BFB\u591A\u5C11\uFF0C\u5176\u5B83\u914D\u7F6E\u540E\u9762\u518D\u5F04\uFF0C\u8FEB\u4E0D\u53CA\u5F85\u5C31\u5148\u5199\u4E0B\u4E86\u8FD9\u7B2C\u4E00\u7BC7\u3002</p>',27),p=[a];function V(u,n,o,h,d,l){return r(),s("div",null,p)}var _=e(i,[["render",V]]);export{c as __pageData,_ as default};
